import{_ as o,c as i,o as e,ae as r}from"./chunks/framework.CDjunVez.js";const p=JSON.parse('{"title":"Control de Cambios","description":"","frontmatter":{"title":"Control de Cambios"},"headers":[],"relativePath":"procedimiento/control-cambios.md","filePath":"procedimiento/control-cambios.md"}'),t={name:"procedimiento/control-cambios.md"};function l(n,a,c,s,d,m){return e(),i("div",null,[...a[0]||(a[0]=[r('<h1 id="control-de-cambios-del-sistema" tabindex="-1">Control de Cambios del Sistema <a class="header-anchor" href="#control-de-cambios-del-sistema" aria-label="Permalink to &quot;Control de Cambios del Sistema&quot;">​</a></h1><h2 id="objetivo" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h2><p>Establecer el proceso formal para registrar, evaluar, aprobar y controlar los cambios solicitados sobre sistemas informáticos en producción o en desarrollo, asegurando la trazabilidad, el control del impacto y la mitigación de riesgos operativos y tecnológicos.</p><hr><h2 id="alcance" tabindex="-1">Alcance <a class="header-anchor" href="#alcance" aria-label="Permalink to &quot;Alcance&quot;">​</a></h2><p>El control de cambios aplica a:</p><ul><li>Sistemas en producción.</li><li>Requerimientos en desarrollo.</li><li>Cambios correctivos, evolutivos, legales o técnicos.</li></ul><p>Este proceso es obligatorio para todos los cambios que puedan afectar:</p><ul><li>Funcionalidad</li><li>Seguridad</li><li>Datos</li><li>Cronograma</li><li>Entregables aprobados</li></ul><hr><h2 id="principios-del-control-de-cambios" tabindex="-1">Principios del Control de Cambios <a class="header-anchor" href="#principios-del-control-de-cambios" aria-label="Permalink to &quot;Principios del Control de Cambios&quot;">​</a></h2><ul><li>Todo cambio debe estar <strong>registrado formalmente</strong>.</li><li>Ningún cambio se implementará sin <strong>evaluación previa</strong>.</li><li>Los cambios deben contar con <strong>aprobación según su impacto</strong>.</li><li>Todo cambio debe ser <strong>trazable</strong> hasta su implementación.</li><li>Debe existir <strong>capacidad de reversa (rollback)</strong> cuando aplique.</li></ul><hr><h2 id="flujo-general-del-control-de-cambios" tabindex="-1">Flujo General del Control de Cambios <a class="header-anchor" href="#flujo-general-del-control-de-cambios" aria-label="Permalink to &quot;Flujo General del Control de Cambios&quot;">​</a></h2><ol><li>Registro del cambio solicitado.</li><li>Análisis del impacto técnico, funcional y operativo.</li><li>Evaluación del impacto en cronograma y entregables.</li><li>Aprobación del cambio por las instancias correspondientes.</li><li>Implementación controlada del cambio.</li><li>Ejecución de pruebas asociadas.</li><li>Actualización de registros y cierre del cambio.</li></ol><hr><h2 id="roles-involucrados" tabindex="-1">Roles Involucrados <a class="header-anchor" href="#roles-involucrados" aria-label="Permalink to &quot;Roles Involucrados&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Rol</th><th>Responsabilidad</th></tr></thead><tbody><tr><td>Área requirente</td><td>Solicitar y justificar el cambio</td></tr><tr><td>Product Owner (PO)</td><td>Evaluar impacto funcional y priorizar</td></tr><tr><td>DTI</td><td>Analizar impacto técnico, implementar y probar</td></tr><tr><td>Comité Funcional</td><td>Aprobar cambios de alto impacto</td></tr><tr><td>Control Interno / Auditoría</td><td>Verificar cumplimiento del procedimiento</td></tr></tbody></table><hr><h2 id="clasificacion-de-cambios" tabindex="-1">Clasificación de Cambios <a class="header-anchor" href="#clasificacion-de-cambios" aria-label="Permalink to &quot;Clasificación de Cambios&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Tipo de cambio</th><th>Descripción</th></tr></thead><tbody><tr><td>Correctivo</td><td>Corrección de errores</td></tr><tr><td>Evolutivo</td><td>Mejora o nueva funcionalidad</td></tr><tr><td>Legal</td><td>Ajuste por normativa</td></tr><tr><td>Técnico</td><td>Ajuste interno sin impacto funcional</td></tr></tbody></table><hr><h2 id="registro-de-cambios" tabindex="-1">Registro de Cambios <a class="header-anchor" href="#registro-de-cambios" aria-label="Permalink to &quot;Registro de Cambios&quot;">​</a></h2><p>Todo cambio deberá registrarse en el <strong>Registro de Cambios del Sistema</strong>, el cual debe incluir como mínimo:</p><ul><li>Descripción del cambio</li><li>Justificación</li><li>Tipo de cambio</li><li>Impacto en cronograma</li><li>Impacto en entregables</li><li>Responsable</li><li>Estado del cambio</li><li>Aprobación correspondiente</li></ul><hr><h2 id="aprobacion-de-cambios" tabindex="-1">Aprobación de Cambios <a class="header-anchor" href="#aprobacion-de-cambios" aria-label="Permalink to &quot;Aprobación de Cambios&quot;">​</a></h2><ul><li>Cambios de bajo impacto: aprobación PO + DTI.</li><li>Cambios de impacto medio: aprobación PO + DTI + área requirente.</li><li>Cambios críticos: aprobación del Comité Funcional.</li></ul><hr><h2 id="relacion-con-el-procedimiento-general" tabindex="-1">Relación con el Procedimiento General <a class="header-anchor" href="#relacion-con-el-procedimiento-general" aria-label="Permalink to &quot;Relación con el Procedimiento General&quot;">​</a></h2><p>El control de cambios se integra transversalmente a las siguientes etapas:</p><ul><li>Etapa 2: Identificación Preliminar del Requerimiento</li><li>Etapa 5: Desarrollo Iterativo</li><li>Etapa 6: Pruebas</li><li>Etapa 7: Paso a Producción</li></ul><hr><h2 id="consideracion-de-control-interno" tabindex="-1">Consideración de Control Interno <a class="header-anchor" href="#consideracion-de-control-interno" aria-label="Permalink to &quot;Consideración de Control Interno&quot;">​</a></h2><p>Ningún cambio podrá ser implementado sin registro, evaluación y aprobación formal.<br> El incumplimiento de este proceso podrá generar observaciones de auditoría y riesgos operativos para la institución.</p>',35)])])}const u=o(t,[["render",l]]);export{p as __pageData,u as default};
