import{_ as e,c as a,o as i,ae as o}from"./chunks/framework.CDjunVez.js";const h=JSON.parse('{"title":"Desarrollo (Etapas)","description":"","frontmatter":{"title":"Desarrollo (Etapas)"},"headers":[],"relativePath":"procedimiento/desarrollo.md","filePath":"procedimiento/desarrollo.md"}'),r={name:"procedimiento/desarrollo.md"};function d(n,t,s,l,c,g){return i(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="_4-desarrollo" tabindex="-1">4. Desarrollo <a class="header-anchor" href="#_4-desarrollo" aria-label="Permalink to &quot;4. Desarrollo&quot;">​</a></h1><h2 id="etapa-principal-–-gobernanza-y-asignacion-de-roles-y-responsabilidades" tabindex="-1">ETAPA PRINCIPAL – Gobernanza y asignación de roles y responsabilidades <a class="header-anchor" href="#etapa-principal-–-gobernanza-y-asignacion-de-roles-y-responsabilidades" aria-label="Permalink to &quot;ETAPA PRINCIPAL – Gobernanza y asignación de roles y responsabilidades&quot;">​</a></h2><p><strong>Objetivo</strong><br> Establecer la estructura de gobernanza, roles, responsabilidades y niveles de decisión que regirán todo el ciclo, garantizando segregación de funciones, trazabilidad y cumplimiento.</p><p><strong>Aplicabilidad</strong><br> Obligatoria para:</p><ul><li>Sistemas nuevos</li><li>Requerimientos o mejoras a sistemas existentes</li></ul><p><strong>Actividades</strong></p><ol><li>Identificación del área requirente y del área propietaria del sistema.</li><li>Asignación formal del PO y su reemplazo.</li><li>Identificación del equipo técnico responsable (DTI).</li><li>Determinación de Comité Funcional (según impacto o criticidad).</li><li>Definición de roles de verificación y control.</li><li>Registro formal de roles y responsabilidades.</li></ol><p><strong>Roles y Responsabilidades (referencial)</strong></p><table tabindex="0"><thead><tr><th>Rol</th><th>Responsabilidad</th></tr></thead><tbody><tr><td>Área Requirente</td><td>Identificar y justificar la necesidad institucional.</td></tr><tr><td>Product Owner</td><td>Validar alcance funcional, priorizar y aceptar entregables.</td></tr><tr><td>DTI</td><td>Diseñar, desarrollar, probar e implementar la solución.</td></tr><tr><td>Comité Funcional</td><td>Aprobar sistemas nuevos y cambios de alto impacto.</td></tr><tr><td>Auditoría / Control Interno</td><td>Verificar cumplimiento y evidencias documentales.</td></tr></tbody></table><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Acta de Asignación de Roles y Responsabilidades</td><td>Anexo_EP01_Asignacion_Roles_y_Responsabilidades</td></tr></tbody></table><p><strong>Control interno</strong><br> No se inicia ninguna etapa posterior sin esta etapa documentada.</p><hr><h2 id="etapa-0-–-identificacion-y-aprobacion-del-sistema-nuevo-solo-sistemas-nuevos" tabindex="-1">ETAPA 0 – Identificación y aprobación del sistema nuevo (solo sistemas nuevos) <a class="header-anchor" href="#etapa-0-–-identificacion-y-aprobacion-del-sistema-nuevo-solo-sistemas-nuevos" aria-label="Permalink to &quot;ETAPA 0 – Identificación y aprobación del sistema nuevo (solo sistemas nuevos)&quot;">​</a></h2><p><strong>Objetivo</strong><br> Autorizar formalmente el sistema nuevo validando necesidad, viabilidad, alineación estratégica, impacto y alcance inicial.</p><p><strong>Actividades</strong></p><ol><li>Identificación y documentación de la necesidad institucional.</li><li>Análisis de impacto organizacional.</li><li>Evaluación de viabilidad: Técnica / Operativa / Legal / Presupuestaria.</li><li>Alcance macro (módulos, exclusiones).</li><li>Ratificación de roles definidos en la Etapa Principal.</li><li>Alineación estratégica institucional.</li><li>Riesgos iniciales y controles.</li></ol><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Acta de Inicio del Sistema Nuevo</td><td>Anexo_E0_Acta_Inicio_Sistema_Nuevo</td></tr></tbody></table><p><strong>Control interno</strong><br> No pasa a Etapa 1 sin el acta aprobada.</p><hr><h2 id="etapa-1-–-planificacion-del-sistema-solo-sistemas-nuevos" tabindex="-1">ETAPA 1 – Planificación del sistema (solo sistemas nuevos) <a class="header-anchor" href="#etapa-1-–-planificacion-del-sistema-solo-sistemas-nuevos" aria-label="Permalink to &quot;ETAPA 1 – Planificación del sistema (solo sistemas nuevos)&quot;">​</a></h2><p><strong>Objetivo</strong><br> Planificar el sistema nuevo: metodología, arquitectura base, cronograma macro, riesgos y estrategias mínimas.</p><p><strong>Actividades</strong></p><ul><li>Roadmap (fases/hitos/entregas).</li><li>Arquitectura base (componentes/integraciones/ambientes).</li><li>Metodología (ágil o híbrida).</li><li>Cronograma macro.</li><li>Riesgos y mitigación.</li><li>Estrategia de pruebas (mínimo funcional + validación de usuario).</li><li>Estrategia de seguridad (mínimo control de accesos y protección).</li></ul><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Plan del Sistema</td><td>Anexo_E1_Plan_Sistema</td></tr></tbody></table><p><strong>Nota MVP</strong><br> Puede ser simplificado, manteniendo alcance, cronograma macro, riesgos y controles mínimos.</p><hr><h2 id="etapa-2-–-identificacion-preliminar-del-requerimiento-solo-sistemas-existentes" tabindex="-1">ETAPA 2 – Identificación Preliminar del Requerimiento (solo sistemas existentes) <a class="header-anchor" href="#etapa-2-–-identificacion-preliminar-del-requerimiento-solo-sistemas-existentes" aria-label="Permalink to &quot;ETAPA 2 – Identificación Preliminar del Requerimiento (solo sistemas existentes)&quot;">​</a></h2><p><strong>Objetivo</strong><br> Registrar, clasificar y priorizar el requerimiento antes del levantamiento detallado.</p><p><strong>Actividades</strong></p><ol><li>Recepción y registro formal de la solicitud.</li><li>Viabilidad inicial (técnica/operativa/normativa).</li><li>Priorización por impacto/urgencia/alineación.</li><li>Confirmación PO y reemplazo.</li><li>Clasificación: Correctivo / Evolutivo / Legal / Emergente.</li></ol><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Solicitud Formal de Requerimiento</td><td>Anexo_E2_Solicitud_Requerimiento</td></tr><tr><td style="text-align:right;">2</td><td>Registro de Cambios del Sistema</td><td>Anexo_E2_Registro_Cambios_Sistema</td></tr></tbody></table><hr><h2 id="etapa-3-–-levantamiento-y-analisis-de-requerimientos" tabindex="-1">ETAPA 3 – Levantamiento y Análisis de Requerimientos <a class="header-anchor" href="#etapa-3-–-levantamiento-y-analisis-de-requerimientos" aria-label="Permalink to &quot;ETAPA 3 – Levantamiento y Análisis de Requerimientos&quot;">​</a></h2><p><strong>Objetivo</strong><br> Levantar, analizar y documentar requerimientos con criterios de aceptación y trazabilidad.</p><p><strong>Actividades</strong></p><ol><li>Revisión de procesos actuales.</li><li>Identificación de brechas.</li><li>Requerimientos funcionales (historias/requerimientos).</li><li>Criterios de aceptación.</li><li>No funcionales (solo obligatorio en sistemas nuevos).</li><li>Matriz de trazabilidad (req → diseño → prueba).</li></ol><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th><th>Aplicabilidad</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Documento de Requerimientos Funcionales</td><td>Anexo_E3_Requerimientos_Funcionales</td><td>Todos</td></tr><tr><td style="text-align:right;">2</td><td>Documento de Requerimientos No Funcionales</td><td>Anexo_E3_Requerimientos_No_Funcionales</td><td>Solo nuevos</td></tr><tr><td style="text-align:right;">3</td><td>Matriz de Trazabilidad Req–Diseño–Prueba</td><td>Anexo_E3_Matriz_Trazabilidad</td><td>Todos</td></tr></tbody></table><hr><h2 id="etapa-4-–-diseno-de-la-solucion" tabindex="-1">ETAPA 4 – Diseño de la Solución <a class="header-anchor" href="#etapa-4-–-diseno-de-la-solucion" aria-label="Permalink to &quot;ETAPA 4 – Diseño de la Solución&quot;">​</a></h2><p><strong>Objetivo</strong><br> Definir la solución técnica/funcional: datos, flujos, interfaces y componentes.</p><p><strong>Actividades</strong></p><ol><li>Diseño de BD (propuesta).</li><li>Mockups/prototipos de pantallas.</li><li>Diseño de interfaces/reportes.</li><li>Revisión y aprobación formal del diseño (con observaciones / rechazo / aprobación).</li></ol><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th><th>Aplicabilidad</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Documento de Diseño de Base de Datos</td><td>Anexo_E4_Diseno_Base_Datos</td><td>Todos</td></tr><tr><td style="text-align:right;">2</td><td>Mockups / Prototipos de Pantallas</td><td>Anexo_E4_Mockups_Pantallas</td><td>Todos</td></tr><tr><td style="text-align:right;">3</td><td>Acta de Revisión y Aprobación del Diseño</td><td>Anexo_E4_Acta_Revision_Aprobacion_Diseno</td><td>Todos</td></tr></tbody></table><hr><h2 id="etapa-5-–-desarrollo-iterativo" tabindex="-1">ETAPA 5 – Desarrollo Iterativo <a class="header-anchor" href="#etapa-5-–-desarrollo-iterativo" aria-label="Permalink to &quot;ETAPA 5 – Desarrollo Iterativo&quot;">​</a></h2><p><strong>Objetivo</strong><br> Desarrollar incrementalmente con control de versiones y validación del PO.</p><p><strong>Actividades</strong></p><ol><li>Desarrollo por iteraciones/sprints.</li><li>Entregas parciales (por defecto, quincenal).</li><li>Validación continua del PO.</li><li>Control de versiones.</li><li>Registro de cambios asociados al sprint.</li></ol><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th><th>Aplicabilidad</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Código fuente / versiones funcionales</td><td>Ver repositorio</td><td>Todos</td></tr><tr><td style="text-align:right;">2</td><td>Registro de versiones</td><td>Sistema de gestión DTI</td><td>Todos</td></tr><tr><td style="text-align:right;">3</td><td>Cronograma detallado</td><td>Anexo_E5_Cronograma_Detallado_Desarrollo</td><td>Todos</td></tr></tbody></table><hr><h2 id="etapa-6-–-pruebas" tabindex="-1">ETAPA 6 – Pruebas <a class="header-anchor" href="#etapa-6-–-pruebas" aria-label="Permalink to &quot;ETAPA 6 – Pruebas&quot;">​</a></h2><p><strong>Objetivo</strong><br> Validar cumplimiento con un enfoque proporcional (mínimo para todos, completo para nuevos/críticos).</p><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th><th>Aplicabilidad</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Plan de Pruebas</td><td>Anexo_E6_Plan_Pruebas</td><td>Todos</td></tr><tr><td style="text-align:right;">2</td><td>Evidencias de Pruebas</td><td>Anexo_E6_Evidencias_Pruebas</td><td>Todos</td></tr><tr><td style="text-align:right;">3</td><td>Registro de No Conformidades</td><td>Anexo_E6_No_Conformidades</td><td>Cuando aplique</td></tr><tr><td style="text-align:right;">4</td><td>Informe Consolidado de Pruebas</td><td>Anexo_E6_Informe_Pruebas</td><td>MVP y nuevos</td></tr></tbody></table><hr><h2 id="etapa-7-–-paso-a-produccion" tabindex="-1">ETAPA 7 – Paso a Producción <a class="header-anchor" href="#etapa-7-–-paso-a-produccion" aria-label="Permalink to &quot;ETAPA 7 – Paso a Producción&quot;">​</a></h2><p><strong>Objetivo</strong><br> Liberar de forma controlada y con reversa, incluyendo scripts BD y roles/permisos cuando aplique.</p><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th><th>Aplicabilidad</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Acta de Paso a Producción (incluye scripts/roles)</td><td>Anexo_E7_Acta_Paso_Produccion</td><td>Todos</td></tr><tr><td style="text-align:right;">2</td><td>Acta de Resultado de Paso a Producción</td><td>Anexo_E7_Acta_Resultado_Paso_Produccion</td><td>Todos</td></tr><tr><td style="text-align:right;">3</td><td>Acta de Aceptación del Usuario</td><td>Anexo_E7_Acta_Acepta_Usuario</td><td>Todos</td></tr><tr><td style="text-align:right;">4</td><td>Acta de Aceptación de QA</td><td>Anexo_E7_Acta_Acepta_QA</td><td>Todos</td></tr><tr><td style="text-align:right;">5</td><td>Plan de Reversa (Rollback)</td><td>Anexo_E7_Plan_Reversa</td><td>MVP y nuevos</td></tr></tbody></table><hr><h2 id="etapa-8-–-entrega-y-capacitacion" tabindex="-1">ETAPA 8 – Entrega y Capacitación <a class="header-anchor" href="#etapa-8-–-entrega-y-capacitacion" aria-label="Permalink to &quot;ETAPA 8 – Entrega y Capacitación&quot;">​</a></h2><p><strong>Objetivo</strong><br> Formalizar entrega y adopción.</p><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th><th>Aplicabilidad</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Acta de Entrega del Sistema/Requerimiento</td><td>Anexo_E8_Acta_Entrega_Sistema</td><td>Todos</td></tr><tr><td style="text-align:right;">2</td><td>Manual de Usuario</td><td>Anexo_E8_Manual_Usuario</td><td>MVP y nuevos</td></tr><tr><td style="text-align:right;">3</td><td>Manual Técnico</td><td>Anexo_E8_Manual_Tecnico</td><td>Solo nuevos</td></tr><tr><td style="text-align:right;">4</td><td>Acta de Capacitación</td><td>Anexo_E8_Acta_Capacitacion</td><td>Cuando aplique</td></tr></tbody></table><hr><h2 id="etapa-9-–-evaluacion-y-mejora-continua" tabindex="-1">ETAPA 9 – Evaluación y Mejora Continua <a class="header-anchor" href="#etapa-9-–-evaluacion-y-mejora-continua" aria-label="Permalink to &quot;ETAPA 9 – Evaluación y Mejora Continua&quot;">​</a></h2><p><strong>Objetivo</strong><br> Evaluar satisfacción y definir mejoras priorizadas.</p><p><strong>Entregables</strong></p><table tabindex="0"><thead><tr><th style="text-align:right;">N°</th><th>Entregable</th><th>Código de anexo</th><th>Aplicabilidad</th></tr></thead><tbody><tr><td style="text-align:right;">1</td><td>Informe de Evaluación Post Implementación</td><td>Anexo_E9_Informe_Evaluacion_Post</td><td>MVP y nuevos</td></tr><tr><td style="text-align:right;">2</td><td>Plan de Mejoras Priorizadas</td><td>Anexo_E9_Plan_Mejoras</td><td>Cuando aplique</td></tr></tbody></table>',76)])])}const b=e(r,[["render",d]]);export{h as __pageData,b as default};
