import{_ as e,c as i,o as l,ae as o}from"./chunks/framework.C-SFMVqu.js";const h=JSON.parse('{"title":"Procedimiento – Etapas","description":"","frontmatter":{"title":"Procedimiento – Etapas"},"headers":[],"relativePath":"procedimiento/etapas.md","filePath":"procedimiento/etapas.md"}'),r={name:"procedimiento/etapas.md"};function t(n,a,s,d,c,u){return l(),i("div",null,[...a[0]||(a[0]=[o('<h1 id="procedimiento-de-gestion-de-requerimientos-y-desarrollo-de-sistemas" tabindex="-1">Procedimiento de Gestión de Requerimientos y Desarrollo de Sistemas <a class="header-anchor" href="#procedimiento-de-gestion-de-requerimientos-y-desarrollo-de-sistemas" aria-label="Permalink to &quot;Procedimiento de Gestión de Requerimientos y Desarrollo de Sistemas&quot;">​</a></h1><h2 id="etapa-principal-–-gobernanza-y-asignacion-de-roles-y-responsabilidades" tabindex="-1">ETAPA PRINCIPAL – Gobernanza y Asignación de Roles y Responsabilidades <a class="header-anchor" href="#etapa-principal-–-gobernanza-y-asignacion-de-roles-y-responsabilidades" aria-label="Permalink to &quot;ETAPA PRINCIPAL – Gobernanza y Asignación de Roles y Responsabilidades&quot;">​</a></h2><h3 id="objetivo" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Establecer la estructura de gobernanza, los roles, responsabilidades y niveles de decisión que regirán todo el ciclo de gestión de requerimientos y desarrollo de sistemas, garantizando segregación de funciones, trazabilidad y control.</p><h3 id="aplicabilidad" tabindex="-1">Aplicabilidad <a class="header-anchor" href="#aplicabilidad" aria-label="Permalink to &quot;Aplicabilidad&quot;">​</a></h3><p>Obligatoria para:</p><ul><li>Sistemas nuevos</li><li>Requerimientos, mejoras o cambios sobre sistemas existentes</li></ul><h3 id="actividades" tabindex="-1">Actividades <a class="header-anchor" href="#actividades" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Identificación del área requirente y del área propietaria del sistema.</li><li>Asignación formal del Product Owner (PO) y su reemplazo.</li><li>Identificación del equipo técnico responsable (DTI).</li><li>Definición de instancias de validación y aprobación.</li><li>Registro formal de roles y responsabilidades.</li></ol><h3 id="entregables" tabindex="-1">Entregables <a class="header-anchor" href="#entregables" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Acta de Asignación de Roles y Responsabilidades (EP01)</li></ul><hr><h2 id="etapa-0-–-identificacion-y-aprobacion-del-sistema-nuevo" tabindex="-1">ETAPA 0 – Identificación y Aprobación del Sistema Nuevo <a class="header-anchor" href="#etapa-0-–-identificacion-y-aprobacion-del-sistema-nuevo" aria-label="Permalink to &quot;ETAPA 0 – Identificación y Aprobación del Sistema Nuevo&quot;">​</a></h2><blockquote><p><strong>Aplicable únicamente para sistemas nuevos</strong></p></blockquote><h3 id="objetivo-1" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-1" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Autorizar formalmente la creación de un nuevo sistema informático, validando su necesidad, viabilidad, alineación estratégica e impacto institucional.</p><h3 id="actividades-1" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-1" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Identificación de la necesidad institucional.</li><li>Análisis de impacto organizacional.</li><li>Evaluación de viabilidad técnica, operativa, legal y presupuestaria.</li><li>Definición del alcance macro del sistema.</li><li>Identificación de riesgos iniciales.</li></ol><h3 id="entregables-1" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-1" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Acta de Inicio del Sistema Nuevo (E0)</li></ul><hr><h2 id="etapa-1-–-planificacion-del-sistema" tabindex="-1">ETAPA 1 – Planificación del Sistema <a class="header-anchor" href="#etapa-1-–-planificacion-del-sistema" aria-label="Permalink to &quot;ETAPA 1 – Planificación del Sistema&quot;">​</a></h2><blockquote><p><strong>Aplicable únicamente para sistemas nuevos</strong></p></blockquote><h3 id="objetivo-2" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-2" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Definir la planificación general del sistema, estableciendo alcance, enfoque metodológico, arquitectura base, cronograma macro y estrategias de control.</p><h3 id="actividades-2" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-2" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Definición del roadmap del sistema.</li><li>Definición de la arquitectura base.</li><li>Selección de metodología de desarrollo.</li><li>Elaboración del cronograma macro.</li><li>Identificación de riesgos principales.</li><li>Definición de estrategias de pruebas y seguridad.</li></ol><h3 id="entregables-2" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-2" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Plan del Sistema (E1)</li></ul><hr><h2 id="etapa-2-–-identificacion-preliminar-del-requerimiento" tabindex="-1">ETAPA 2 – Identificación Preliminar del Requerimiento <a class="header-anchor" href="#etapa-2-–-identificacion-preliminar-del-requerimiento" aria-label="Permalink to &quot;ETAPA 2 – Identificación Preliminar del Requerimiento&quot;">​</a></h2><blockquote><p><strong>Aplicable para sistemas existentes</strong></p></blockquote><h3 id="objetivo-3" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-3" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Registrar, analizar y priorizar preliminarmente los requerimientos solicitados por las áreas usuarias.</p><h3 id="actividades-3" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-3" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Recepción y registro formal del requerimiento.</li><li>Análisis inicial de viabilidad.</li><li>Priorización del requerimiento.</li><li>Confirmación del Product Owner.</li><li>Clasificación del requerimiento (correctivo, evolutivo, legal o emergente).</li></ol><h3 id="entregables-3" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-3" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Solicitud Formal de Requerimiento (E2)</li><li>Registro de Cambios del Sistema (E2)</li></ul><hr><h2 id="etapa-3-–-levantamiento-y-analisis-de-requerimientos" tabindex="-1">ETAPA 3 – Levantamiento y Análisis de Requerimientos <a class="header-anchor" href="#etapa-3-–-levantamiento-y-analisis-de-requerimientos" aria-label="Permalink to &quot;ETAPA 3 – Levantamiento y Análisis de Requerimientos&quot;">​</a></h2><h3 id="objetivo-4" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-4" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Documentar y validar los requerimientos funcionales y no funcionales, asegurando su correcta definición y trazabilidad.</p><h3 id="actividades-4" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-4" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Revisión de procesos actuales.</li><li>Identificación de brechas.</li><li>Documentación de requerimientos funcionales.</li><li>Definición de criterios de aceptación.</li><li>Identificación de requerimientos no funcionales (cuando aplique).</li><li>Elaboración de la matriz de trazabilidad.</li></ol><h3 id="entregables-4" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-4" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Requerimientos Funcionales (E3)</li><li>Requerimientos No Funcionales (E3 – solo sistemas nuevos)</li><li>Matriz de Trazabilidad (E3)</li></ul><hr><h2 id="etapa-4-–-diseno-de-la-solucion" tabindex="-1">ETAPA 4 – Diseño de la Solución <a class="header-anchor" href="#etapa-4-–-diseno-de-la-solucion" aria-label="Permalink to &quot;ETAPA 4 – Diseño de la Solución&quot;">​</a></h2><h3 id="objetivo-5" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-5" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Definir la solución técnica y funcional que permitirá implementar los requerimientos aprobados.</p><h3 id="actividades-5" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-5" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Diseño de la estructura de base de datos.</li><li>Elaboración de modelos de información.</li><li>Diseño de mockups o prototipos de pantallas.</li><li>Revisión y validación del diseño.</li></ol><h3 id="entregables-5" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-5" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Diseño de Base de Datos (E4)</li><li>Mockups / Prototipos (E4)</li><li>Acta de Revisión y Aprobación del Diseño (E4)</li></ul><hr><h2 id="etapa-5-–-desarrollo-iterativo" tabindex="-1">ETAPA 5 – Desarrollo Iterativo <a class="header-anchor" href="#etapa-5-–-desarrollo-iterativo" aria-label="Permalink to &quot;ETAPA 5 – Desarrollo Iterativo&quot;">​</a></h2><h3 id="objetivo-6" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-6" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Construir la solución de forma incremental, validando continuamente con el Product Owner.</p><h3 id="actividades-6" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-6" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Desarrollo por iteraciones o sprints.</li><li>Entregas parciales funcionales.</li><li>Validación continua con el PO.</li><li>Gestión de versiones y control de cambios.</li><li>Elaboración del cronograma detallado.</li></ol><h3 id="entregables-6" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-6" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Código fuente (repositorio)</li><li>Versiones funcionales</li><li>Registro de versiones</li><li>Cronograma Detallado (E5)</li></ul><hr><h2 id="etapa-6-–-pruebas" tabindex="-1">ETAPA 6 – Pruebas <a class="header-anchor" href="#etapa-6-–-pruebas" aria-label="Permalink to &quot;ETAPA 6 – Pruebas&quot;">​</a></h2><h3 id="objetivo-7" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-7" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Validar que el sistema cumple con los requerimientos definidos antes de su puesta en producción.</p><h3 id="enfoque-de-proporcionalidad" tabindex="-1">Enfoque de Proporcionalidad <a class="header-anchor" href="#enfoque-de-proporcionalidad" aria-label="Permalink to &quot;Enfoque de Proporcionalidad&quot;">​</a></h3><ul><li>Cambios menores: pruebas funcionales básicas.</li><li>MVP: pruebas funcionales y validación de usuario.</li><li>Sistemas nuevos: pruebas funcionales, técnicas y de seguridad.</li></ul><h3 id="actividades-7" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-7" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Definición del alcance de pruebas.</li><li>Elaboración del plan de pruebas.</li><li>Ejecución de pruebas.</li><li>Registro de evidencias y no conformidades.</li><li>Validación de correcciones.</li></ol><h3 id="entregables-7" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-7" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Plan de Pruebas (E6)</li><li>Evidencias de Pruebas (E6)</li><li>Registro de No Conformidades (E6)</li><li>Informe Consolidado de Pruebas (E6)</li></ul><hr><h2 id="etapa-7-–-paso-a-produccion" tabindex="-1">ETAPA 7 – Paso a Producción <a class="header-anchor" href="#etapa-7-–-paso-a-produccion" aria-label="Permalink to &quot;ETAPA 7 – Paso a Producción&quot;">​</a></h2><h3 id="objetivo-8" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-8" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Autorizar y ejecutar la puesta en producción de forma controlada y segura.</p><h3 id="actividades-8" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-8" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Revisión final del desarrollo y pruebas.</li><li>Autorización formal.</li><li>Migración de base de datos (si aplica).</li><li>Configuración de roles y permisos.</li><li>Ejecución del paso a producción.</li><li>Activación del plan de reversa en caso de fallos.</li></ol><h3 id="entregables-8" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-8" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Acta de Paso a Producción (E7)</li><li>Acta de Resultado de Producción (E7)</li><li>Actas de Aceptación (Usuario / QA)</li><li>Plan de Reversa (E7)</li></ul><hr><h2 id="etapa-8-–-entrega-y-capacitacion" tabindex="-1">ETAPA 8 – Entrega y Capacitación <a class="header-anchor" href="#etapa-8-–-entrega-y-capacitacion" aria-label="Permalink to &quot;ETAPA 8 – Entrega y Capacitación&quot;">​</a></h2><h3 id="objetivo-9" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-9" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Formalizar la entrega del sistema y asegurar su correcta adopción.</p><h3 id="actividades-9" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-9" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Entrega formal del sistema.</li><li>Capacitación a usuarios.</li><li>Entrega de documentación.</li><li>Registro de aceptación.</li></ol><h3 id="entregables-9" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-9" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Acta de Entrega del Sistema (E8)</li><li>Manual de Usuario (E8)</li><li>Manual Técnico (E8)</li><li>Acta de Capacitación (E8)</li></ul><hr><h2 id="etapa-9-–-evaluacion-y-mejora-continua" tabindex="-1">ETAPA 9 – Evaluación y Mejora Continua <a class="header-anchor" href="#etapa-9-–-evaluacion-y-mejora-continua" aria-label="Permalink to &quot;ETAPA 9 – Evaluación y Mejora Continua&quot;">​</a></h2><h3 id="objetivo-10" tabindex="-1">Objetivo <a class="header-anchor" href="#objetivo-10" aria-label="Permalink to &quot;Objetivo&quot;">​</a></h3><p>Evaluar el desempeño del sistema e identificar oportunidades de mejora.</p><h3 id="actividades-10" tabindex="-1">Actividades <a class="header-anchor" href="#actividades-10" aria-label="Permalink to &quot;Actividades&quot;">​</a></h3><ol><li>Evaluación de satisfacción del usuario.</li><li>Revisión de experiencia de usuario.</li><li>Identificación de mejoras.</li><li>Priorización de acciones.</li></ol><h3 id="entregables-10" tabindex="-1">Entregables <a class="header-anchor" href="#entregables-10" aria-label="Permalink to &quot;Entregables&quot;">​</a></h3><ul><li>Informe de Evaluación Post Implementación (E9)</li><li>Plan de Mejoras Priorizadas (E9)</li></ul>',96)])])}const m=e(r,[["render",t]]);export{h as __pageData,m as default};
